    private static final Multimap<String, FileType> DEFINED_TYPES;

    static {
        Multimap<String, FileType> definedTypes = LinkedHashMultimap.create();
        Field[] fields = FileTypes.class.getFields();
        for (Field field : fields) {
            if (!FileType.class.equals(field.getType())) {
                continue;
            }
            int modifiers = field.getModifiers();
            if (Modifier.isFinal(modifiers) && Modifier.isStatic(modifiers)) {
                try {
                    FileType value = (FileType) field.get(null);
                    definedTypes.put(value.extension.toLowerCase(), value);
                } catch (IllegalAccessException e) {
                    //ignore
                }
            }
        }
        DEFINED_TYPES = definedTypes;
    }

    public static Optional<FileType> checkType(File file) {
        final String ext = FilenameUtils.getExtension(file.getName().toLowerCase());
        for (FileType type : DEFINED_TYPES.get(ext)) {
            if(type.match(file)){
                return Optional.of(type);
            }
        }
        return Optional.empty();
    }
